 */

  // functions for INSERTING AND DELETING from the SONG table
  const insertSong = () => {
    Axios.post("http://localhost:3002/api/insert", {
      songName: songName,
    }).then(() => {
      alert("song inserted");
    });
  };

  return (
    <div className="App">
      <div>
        <div className="form">
          <label>Song Title:</label>
          <input
            type="text"
            name="songName"
            onChange={(e) => {
              setSongName(e.target.value);
            }}
          ></input>
          <button onSubmit={insertSong}>Insert Song</button>
          <button onSubmit={deleteSong}>Delete Song</button>

          <label>Artist Name </label>
          <input type="text" name="artistName"></input>
          <button> All Albums + Songs for an Artist </button>
          <label>User Name </label>
          <input type="text" name="userName"></input>
          <button> All Playlists for a User </button>
        </div>
      </div>

      {/* {songList.map((val) => {
          return (
            <div className="card">
