import "./App.css";
import React, { useState, useEffect } from "react";
import Axios from "axios";

function App() {
  const [movieName, setMovieName] = useState("");
  const [Review, setReview] = useState("");
  const [songList, setsongList] = useState([]);
  const [newDanceVal, setnewDanceVal] = useState("");
  const [songName, setsongName] = useState("");

  useEffect(() => {
    Axios.get("http://localhost:3002/api/get").then((response) => {
      setsongList(response.data);
    });
  }, []);

  const insertSong = () => {
    Axios.post("http://localhost:3002/api/insert", {
      songName: songName,
    });
  };

  const deleteSong = (songName) => {
    Axios.delete(`http://localhost:3002/api/delete/${songName}`);
  };

  const updateDanceVal = (songName) => {
    Axios.put(`http://localhost:3002/api/update`, {
      songName: songName,
      newDanceVal: newDanceVal,
    });
  };

  return (
    <div className="App">
      <h1> CRUD APPLICATIONS</h1>

      <div className="form">
        <label> Song Name:</label>
        <input
          type="text"
          name="movieName"
          onChange={(e) => {
            setsongName(e.target.value);
          }}
        />
        <button onClick={insertSong}> Insert</button>
        <button>SQL Query 1</button>;<button>SQL Query 2</button>;
        {songList.map((val) => {
          return (
            <div className="card">
              <h3> Song Name: {val.song_name} </h3>
              <p>Dance Value: {val.song_dance}</p>
              <button
                onClick={() => {
                  deleteSong(val.song_name);
                }}
              >
                {" "}
                Delete
              </button>
              <input
                type="number"
                id="updateDanceInput"
                onChange={(e) => {
                  setnewDanceVal(e.target.value);
                }}
              />
              <button
                onClick={() => {
                  updateDanceVal(val.song_name);
                }}
              >
                {" "}
                Update
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
