import "./App.css";
import React, { useState, useEffect } from "react";
import Axios from "axios";

function App() {
  const [movieName, setMovieName] = useState("");
  const [Review, setReview] = useState("");
  const [songList, setsongList] = useState([]);
  const [newReview, setNewReview] = useState("");
  const [songName, setsongName] = useState("");

  useEffect(() => {
    Axios.get("http://localhost:3002/api/get").then((response) => {
      setsongList(response.data);
    });
  }, []);

  const insertSong = () => {
    Axios.post("http://localhost:3002/api/insert", {
      songName: songName,
    });
  };

  const deleteSong = (songName) => {
    Axios.delete(`http://localhost:3002/api/delete/${songName}`);
  };

  /* const updateReview = (movieName) => {
    Axios.put(`http://localhost:3002/api/update`, {
      movieName: movieName,
      movieReview: newReview,
    });
    setNewReview("");
  }; */

  return (
    <div className="App">
      <h1> CRUD APPLICATIONS</h1>

      <div className="form">
        <label> Song Name:</label>
        <input
          type="text"
          name="movieName"
          onChange={(e) => {
            setsongName(e.target.value);
          }}
        />

        <button onClick={insertSong}> Insert</button>
        <button onClick={deleteSong}> Delete</button>

        {songList.map((val) => {
          return (
            <div className="card">
              <h1> Song Name: {val.song_name} </h1>
              <p>Artist Name: {val.artist_id}</p>
              <p>Dance Value: {val.song_dance}</p>
              <button
                onClick={() => {
                  deleteReview(val.movieName);
                }}
              >
                {" "}
                Delete
              </button>
              <input
                type="text"
                id="updateInput"
                onChange={(e) => {
                  setNewReview(e.target.value);
                }}
              />
              <button
                onClick={() => {
                  updateReview(val.movieName);
                }}
              >
                {" "}
                Update
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
